BEGIN	

	DECLARE @HashKey VARCHAR(36)
	SET @HashKey = '00000000-0000-0000-0000-000000000000'

	SELECT 
		ID_USUARIO,RTRIM(NOMBRE) AS NOMBRE,RTRIM(CONTRASENIA) AS CONTRASEÑA,'[MANAGER]' AS PERFIL,
		@HashKey AS ID_EMPRESA,@HashKey AS CODIGO_INTEGRACION,FECHA_REGISTRO,FECHA_MODIFICACION,
		@HashKey AS HashKey, MAIL,'[MULTIPLE]' AS NOMBRE_EMPRESA, '[MULTIPLE]' AS ID_CATEGORIA_SERVICIO
	FROM ADMINISTRADORES 
	WHERE NOMBRE = @Nombre 
